import React, { useState } from 'react';
import './App.css';
import pokedex from './images/pokedex.png';
import pikachu from './images/pikachu.png';
import {Link} from 'react-router-dom';
import Button from '@material-ui/core/Button';

const Header = () => {
    const [state, setState] = useState({
        toggleIn:{
            background: '#efefef',
            color: 'rgb(107, 102, 102)'
        },
        toggleOut:{
            background: 'rgb(107, 102, 102)',
            color: '#efefef'
        }
    });
    const[toggle, setToggle] =  useState(false);

    const styling = {
        fontSize: '20px',
        float: 'left',
        display: 'block'
    }
    return(
        <div>
            <header>
            <img src={pokedex} className='pokedex' alt="logo"/>
            <img src={pikachu} className="pikachu" alt='pik'/>
            </header>
            <nav style={toggle ? state.toggleIn : state.toggleOut}>
                <Button
                style={styling}
                size="large" 
                variant="contained"
                color={toggle ? 'disabled' : 'default'}
                onClick={() => setToggle(!toggle)}>
                    {toggle ? "Toogle Out" : "Toggle In"}
                </Button>
                <Link to="about">About</Link>
                <Link to="pokemonlist">Pokemon List</Link>
                <Link to='/'>Home</Link>
            </nav>
        </div>
    )
}

export default Header;
