import React, { useState } from 'react';
import './App.css';
import pokedex from './images/pokedex.png';
import pikachu from './images/pikachu.png';
import {Link} from 'react-router-dom';
import Button from '@material-ui/core/Button';

const Header = () => {
    const [state, setState] = useState({
        toggleIn:{
            backgroundColor: '#efefef',
        },
        toggleOut:{
            backgroundColor: 'rgb(107, 102, 102)',
        }
    });
    const[toggle, setToggle] =  useState(false);

    const styling = {
        fontSize: '20px'
    }
    return(
        <div style={toggle ? state.toggleIn : state.toggleOut}>
            <header>
            <img src={pokedex} className='pokedex' alt="logo"/>
            <img src={pikachu} className="pikachu" alt='pik'/>
            </header>
            <nav>
                <Button
                size="large" 
                variant="contained"
                color={toggle ? 'primary' : 'secondary'}
                onClick={() => setToggle(!toggle)}
                className="togglebtn">
                    {toggle ? "Toogle In" : "Toggle Out"}
                </Button>
                <Link to="about">About</Link>
                <Link to="pokemonlist">Pokemon List</Link>
                <Link to='/'>Home</Link>
            </nav>
        </div>
    )
}

export default Header;
