import React from 'react';
import Pokemon from './pokemon';
import './App.css';
import Home from './home';
import Header from './header';

const App = () => {
    const [state, setState] = React.useState(false);
    const [state2, setState2] = React.useState(false);

    /*const showHome = () => {
        setState2(false);
        setState(!state);
    }*/

    const showPokemonList = () => {
        setState(!state);
        setState2(false);
    }

    const showAbout = () => {
        setState2(!state2);
        setState(false);
    }

    return(
        <div>
            <Header 
            showpokemonlist={showPokemonList}
            showabout={showAbout} />
            <Home />
            {state && <Pokemon />}
            {state2 && <p>This is a pokedex app made on React JS</p>}
        </div>
    )
}

export default App;
