import React from 'react';
import Pokemon from './pokemon';
import './App.css';
import pokedex from './images/pokedex.png';
import pokemon from './images/pokemon.png';
import Button from '@material-ui/core/Button'

const Home = () => {
    const [state, setState] = React.useState(false);
    const [state2, setState2] = React.useState(false);
    const [state3, setState3] = React.useState(false);

    const showHome = () => {
        setState3(!state3);
        setState2(false);
        setState(false);
    }

    const showPokemonList = () => {
        setState(!state);
        setState2(false);
        setState3(false);
    }

    const showAbout = () => {
        setState2(!state2);
        setState(false);
        setState3(false);
    }

    return(
        <div>

            <header>
            <img src={pokedex} alt="logo"/>

            <nav>
                <a onClick={showHome}>Home</a>
                <a onClick={showPokemonList}>Pokemon List</a>
                <a onClick={showAbout}>About</a>
            </nav>
            </header>
            {state3 && <img src={pokemon} className='pok' alt="home"/>}
            {state && <Pokemon />}
            {state2 && <p>This is a pokedex app made on React JS</p>}
        </div>
    )
}

export default Home;
