import React from 'react';
import Card from '@material-ui/core/Card';
import CardActionArea from '@material-ui/core/CardActionArea';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import './App.css';


const PokemonList = ({pokemon}) => {
    const pokemonlist = {
          maxWidth: 345,
          display: "flex",
          justifyContent: 'space-around',
          flexWrap: 'wrap',
          margin: '15px 0',
          width: '100%',
          textTransform: 'uppercase',
          fontFamily: 'Arial, Helvetica, sans-serif'        
      };

      const card = {
        maxWidth: '300px',
        width: '100%',
        height: '300px',
        cursor: 'pointer',
        overflow: 'hidden',
        padding: '15px',
        margin: '15px 0',
        fontFamily: 'Arial, Helvetica, sans-serif',
        textAlign: 'center',
      }

      const button = {
        padding: '12px 12px',
        width: '100%',
        fontSize: '15px',
        textTransform: 'uppercase'
      }
    return(
        <div style={pokemonlist} className='pokemonList'>
            {/*pokemon.map(p => (
                <div className="card" key={Math.random()}>
                <div className="pokemon-name" key={p}>{p}</div>
                <button className="btn btn-danger btn-lg">See Details</button>
                </div>
            ))*/}

        {pokemon.map(p => (
            <Card style={card}>
            <CardActionArea>
              <CardContent>
                <Typography gutterBottom variant="h5" component="h2">
                  {p}
                </Typography>
              </CardContent>
            </CardActionArea>
            <CardActions>
              <Button size="large" 
              color="primary" 
              style={button}
              variant='outlined'
              >
                See Details
              </Button>
            </CardActions>
          </Card>
        ))}
        </div>
    )
}

export default PokemonList;
