import React, { useState } from 'react';
import './App.css';
import pokedex from './images/pokedex.png';
import pikachu from './images/pikachu.png';
import {Link} from 'react-router-dom';

const Header = () => {
    const [state, setState] = useState(false);
    const[toggle, setToggle] =  useState({
        toggleIn:{
            background: '#ffffff',
            color: 'rgb(107, 102, 102)'
        },
        toggleOut:{
            background: 'rgb(107, 102, 102)',
            color: '#ffffff'
        }
    })
    const toggling = () => {
        setToggle(state ? toggle.toggleIn : toggle.toggleOut)
    }
    return(
        <div>
            <header>
            <img src={pokedex} className='pokedex' alt="logo"/>
            <img src={pikachu} className="pikachu" alt='pik'/>
            </header>
            <nav>
                <button 
                onClick={() => setState(!state)}
                style={toggling}>{!state ? "Toogle In" : "Toggle Out"}</button>
                <Link to="about">About</Link>
                <Link to="pokemonlist">Pokemon List</Link>
                <Link to='/'>Home</Link>
            </nav>
        </div>
    )
}

export default Header;
